import {useEffect, useState, useRef} from 'react';

export default function Projects(){
  var [mouseDown, changeMouseDown] = useState(0);
  var [prevPos, changePrevPos] = useState(0);
  var [translateX, changeTranslateX] = useState(0);
  var [nextpercentage, changeNext] = useState(0);

  var trackRef = useRef();

  var images = [];



  useEffect(() => {
  var track = trackRef.current;
  const handleOnDown = e => track.mousedownat = e.clientX;



  const handleOnUp = () => {
    track.mousedownat = "0";
    track.prevpercentage = track.percentage;
  }

  const handleOnMove = e => {
    if(track.mousedownat === "0") return;

    const mouseDelta = parseFloat(track.mousedownat) - e.clientX,
          maxDelta = window.innerWidth / 2;

    const percentage = (mouseDelta / maxDelta) * -100,
          nextPercentageUnconstrained = parseFloat(track.prevpercentage) + percentage,
          nextPercentage = Math.max(Math.min(nextPercentageUnconstrained, 0), -100);

    track.percentage = nextPercentage;

    track.animate({
      transform: `translate(${nextPercentage}%, -50%)`
    }, { duration: 1200, fill: "forwards" });

    for(var i = 0; i < 7; i++){
      console.log('working');
      images.push(<Image translateX={nextPercentage + 100}/>);
    }

  }

  window.onmousedown = e => handleOnDown(e);

window.ontouchstart = e => handleOnDown(e.touches[0]);

window.onmouseup = e => handleOnUp(e);

window.ontouchend = e => handleOnUp(e.touches[0]);

window.onmousemove = e => handleOnMove(e);

window.ontouchmove = e => handleOnMove(e.touches[0]);
  });
  return(
    <div className={"container"} >
    <div id="image-track" mousedownat="0" prevpercentage="0">
      {images}
    </div>

    </div>
  );
}


function Image({translateX}){
  var imageanimate = useRef();

  useEffect(() => {
    imageanimate.current.animate({objectPosition: `${translateX}% center`}, {duration: 1200, fill: 'forwards'});
  });
  return(
  <img ref={imageanimate} className="image" src="https://images.unsplash.com/photo-1672522597762-63bb63b4f1d4?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1470&q=80"
      draggable='false'/>
    );
}
