      var direction = 0;
      if(e.deltaY < 0){
        //scrolling up
        direction = 1;
      }
      if(e.deltaY > 0){
        //scrolling down
        direction = -1;
      }
      mouse.distance = 1;
      mouse.dx = 8 * direction;
    }

