      w = 400; // keep at 320
      h = 400;
      */
      //scale
      /*
      imgHeight = img.height - img.height/20;
      sx = img.width/(img.width/imgHeight * h/w);

      */
      //x, y coords
      this.x = (canvas.width/6 + (this.w * arrayPosition)) + margin + posx;
      this.y = canvas.height/2 - this.h/2;

      if(mouse.clicked){
        var wdx = 0.1;
        var idx = 0.1;
        if(this.x > 0){
          this.x -= wdx;
        }
        if(this.y > 0){
          this.y -= wdx;
        }
        if(this.w < canvas.width){
          this.w += idx;
        }
        if(this.h < canvas.height){
          this.h += idx;
        }

        console.log('working');

      }

      context.save();
        const rect = new Path2D();
        rect.rect(this.x, this.y, this.w, this.h);
        context.clip(rect);
        context.drawImage(img, this.x + parallax, this.y, img.width/10, img.height/10);
      context.restore();
      //context.drawImage(img, img.width/3 + parallax, (img.height/20)/2, sx, imgHeight, x, y, w, h);

      //If true, the image is in view
      if(this.x < canvas.width/2 && this.x + this.w > canvas.width/2){
        projectIndex = arrayPosition;
        //check if hovering
          if(mouse.x > this.x &&  mouse.x < this.x + this.w){
            if(mouse.y > this.y && mouse.y < this.y + this.h ){
              mouse.hovering = true;
            }
            else{
              mouse.hovering = false;
            }
