            this.fps = 0;
          }

          c.font = "400 12px GT America Mono";
          c.fillStyle = "rgba(244, 244, 244, 1)";
          c.fill();
          c.fillText(this.jumble,this.x, this.y + 20);

          this.fps++;
        }


  }

  function drawNav(w){
    var rem = 16;
    var length = Math.floor(w/rem);
    var h = canvas.height - (4*rem);
    c.save();
      //Move origin to bottom center
      c.translate(canvas.width/2 - w/2, h);

      //Current project out of x being viewed
      c.font = "400 12px GT America Mono";
      c.fillStyle = "rgba(244, 244, 244, 1)";
      c.fill();
      c.fillText('0' + projectIndex,0, 8);
      c.fillText('0' + 5,w - 24, 8);

      const rect = new Path2D();
      rect.rect(100, -12.5, 200, 50);
      c.clip(rect);

      //Iterate by project total * 5
      for(var i = 0; i <= length; i++){
        var x = i*rem + posx/rem;
        c.beginPath();
        //Height is function of position
        if(x > w/2 - 10 && x <= w/2){
          c.moveTo(x, -2);
          c.lineTo(x, 10);
        }
        else if(x > w/2 - 30 && x < w/2 + 10){
          c.moveTo(x, -1);
          c.lineTo(x, 9);
        }
        else{
          c.moveTo(x, 0);
          c.lineTo(x, 8);
        }
        c.strokeStyle = "#f4f4f4";
        c.stroke();
      }

      //Left Gradient
      const g1 = c.createLinearGradient(100, 0, 200, 0);
      g1.addColorStop(0, 'rgba(0,0,0,1)');
      g1.addColorStop(1, 'rgba(0,0,0,0)');

      //Right Gradient
      const g2 = c.createLinearGradient(200, 0, 300, 0);
      g2.addColorStop(0, 'rgba(0,0,0,0)');
      g2.addColorStop(1, 'rgba(0,0,0,1)');

      c.fillStyle = g1;
      c.fillRect(100, -2, 100, 20);

      c.fillStyle = g2;
      c.fillRect(200, -2, 100, 20);
    c.restore();
  }

  //Probably want to load these from a text file
  var header1 = new Header("THOUGHT POLICE", "IMMERSION");
  var header2 = new Header("LA BATIE", "WEB DESIGN");
  var header3 = new Header("ITALIST", "UX DESIGN");
  var header4 = new Header("DEAR VAN GOGH", "IMMERSIVE ENVIRONMENT");
  var header5 = new Header("LAMPANELLA", "INDUSTRIAL DESIGN");

  var img1 = new ImageBox();
  var img2 = new ImageBox();
  var img3 = new ImageBox();
  var img4 = new ImageBox();
  var img5 = new ImageBox();

  //Animates Everything
  function draw(){
      img1.image(c, 1, header1);
      img2.image(c, 2, header2);
      img3.image(c, 3, header3);
      img4.image(c, 4, header4);
      img5.image(c, 5, header5);

      drawNav(400);
  }

  function animate(){
    requestAnimationFrame(animate);

    c.clearRect(0, 0, canvas.width, canvas.height);

    //Limit distance
    if(posx != mouse.distance){
      if((mouse.dx < 0 && posx > -(canvas.width - 160)) || (mouse.dx > 0 && posx < 440)){
        posx += mouse.dx;
        mouse.dx *= 0.97;
      }
    }
    //draw
    draw();
  }

  animate();
  });
  return(
    <>
    <h3 ref={cursorRef} className="cursor">open </h3>
    <canvas ref={canvasRef} id="canvas" width={0} height={0}></canvas>
    </>
  )
}
