import {useEffect, useState} from 'react';

export default function Projects(){
  var [mouseDown, changeMouseDown] = useState(0);
  var [prevPos, changePrevPos] = useState(0);
  var [translateX, changeTranslateX] = useState(0);
  var [nextpercentage, changeNext] = useState(0);

  var images = [];

  for(var i = 0; i < 7; i++){
    images.push(<Image translateX={nextpercentage + 100}/>);
  }

  useEffect(() => {
    window.onmousemove = e => {
      if(mouseDown === 0) return;

      const mouseDelta = parseFloat(mouseDown) - e.clientX;
      const maxDelta = window.innerWidth / 2;
      const percentage = (mouseDelta / maxDelta) * -100;
      const final = parseFloat(prevPos) + percentage;

      if(final > 0){
        final = 0;
      }
      else if(final < -100){
        final = -100;
      }
      changeNext(final);

      if(nextpercentage > 0){
        changeNext(0);
      }

      changeTranslateX(nextpercentage);

    }

    window.onmousedown = e =>{
      changeMouseDown(e.clientX);
    }

    window.onmouseup = e =>{
      changeMouseDown(0);
      changePrevPos(nextpercentage);
    }
  });
  return(
    <div className={"container"} >
      <div className={'image-track'} style={{transform: `translate(${translateX}%, -50%)`}}>
        {images}
      </div>
    </div>
  );
}


function Image({translateX}){
  return(
  <img className="image" src="https://images.unsplash.com/photo-1672522597762-63bb63b4f1d4?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1470&q=80"
        style={{objectPosition: `${translateX}% 50%`}}
      draggable='false'/>
    );
}
