import {useEffect, useRef, useState} from 'react';

export default function Projects(){
  var track = useRef(null);
  var [mouseDown, changeMouseDown] = useState(0);
  var [prevPos, changePrevPos] = useState(0);
  var [translateX, changeTranslateX] = useState(0);
  var [nextpercentage, changeNext] = useState(0);
  useEffect(() => {
    window.onmousemove = e => {
      if(mouseDown === 0) return;

      const mouseDelta = parseFloat(mouseDown) - e.clientX;
      const maxDelta = window.innerWidth / 2;

      const percentage = (mouseDelta / maxDelta) * -100;

      const final = parseFloat(prevPos) + percentage;
      if(final > 0){
        final = 0;
      }
      else if(final < -100){
        final = -100;
      }
      changeNext(final);

      if(nextpercentage > 0){
        changeNext(0);
      }

      changeTranslateX(nextpercentage);
    }

    window.onmousedown = e =>{
      changeMouseDown(e.clientX);
    }

    window.onmouseup = e =>{
      changeMouseDown(0);
    }
  });
  return(
    <div className={"container"} >
      <div ref={track} className={'image-track'} style={{transform: `translate(${translateX}%, -50%)`}}>
        <img className="image" src="https://images.unsplash.com/photo-1677577441597-a446a5b99df8?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=687&q=80" draggable='false'/>
        <img className="image" src="https://images.unsplash.com/photo-1677577441597-a446a5b99df8?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=687&q=80" draggable='false'/>
        <img className="image" src="https://images.unsplash.com/photo-1677577441597-a446a5b99df8?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=687&q=80" draggable='false'/>
        <img className="image" src="https://images.unsplash.com/photo-1677577441597-a446a5b99df8?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=687&q=80" draggable='false'/>
        <img className="image" src="https://images.unsplash.com/photo-1677577441597-a446a5b99df8?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=687&q=80" draggable='false'/>
        <img className="image" src="https://images.unsplash.com/photo-1677577441597-a446a5b99df8?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=687&q=80" draggable='false'/>
        <img className="image" src="https://images.unsplash.com/photo-1677577441597-a446a5b99df8?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=687&q=80" draggable='false'/>
      </div>
    </div>
  );
}
