import {useRef, useEffect} from 'react';

export default function Projects(){
  const canvasRef = useRef(null);

  useEffect(() => {
    // GLOBAL VARIABLES
    var posx = 0;
    var fps = 60;
    var projectIndex = 1;

    var mouse = {
      x: 0,
      y: undefined,
      dx: 2,
      down: false,
      downPos: 0,
      distance: 0,
    }

    var canvas = canvasRef.current;

    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    window.onresize = e => {
      canvas.height = window.innerHeight;
      canvas.width = window.innerWidth;
    }

    var c = canvas.getContext('2d');

    //Temp img
    let img = new Image();
    img.src = 'test.jpg';

    canvas.onmousemove = e => {
      //If not mouse down, then just return
      if(!mouse.down) return;
      e.preventDefault();

      //otherwise, is dragging
      setTimeout(function(){
        mouse.downPos = e.x;
      }, 100);

      mouse.distance = e.x - mouse.downPos;
      var direction = 1;

      if(mouse.distance < 0) direction = -1;
      mouse.dx = Math.abs(mouse.distance/20) * direction;

    };
    //drag start
    canvas.onmousedown = e =>{
      if(!mouse.down){
        mouse.downPos = e.x;
      }
      mouse.down = true;

    }

    canvas.onmouseup = e =>{
      mouse.down = false;
      mouse.prevPos = mouse.x;
    }

    //If Scroll
    canvas.onwheel = e =>{
      var direction = 0;
      if(e.deltaY < 0){
        //scrolling up
        direction = 1;
      }
      if(e.deltaY > 0){
        //scrolling down
        direction = -1;
      }
      mouse.distance = 1;
      mouse.dx = 8 * direction;
    }

    var offset = 28;
    function image(context, arrayPosition, h1){
      var w, h;
      var sx, sy;
      var imgWidth, imgHeight;
      var x, y;

      //margin between cards
      var margin = 40*arrayPosition;

      //parallax distance
      var parallax = img.width/3 *(posx / (canvas.width));

      imgHeight = img.height - img.height/20;

      //dimensions of card
      w = 400; // keep at 320
      h = 400;

      //scale
      sx = img.width/(img.width/imgHeight * h/w);

      //x, y coords
      x = (canvas.width/6 + (w * arrayPosition)) + margin + posx;
      y = canvas.height/2 - h/2;
      context.drawImage(img, img.width/3 + parallax, (img.height/20)/2, sx, imgHeight, x, y, w, h);

      //If true, the image is in view
      if(x < canvas.width/2 && x + w > canvas.width/2){
        projectIndex = arrayPosition;
      }

      //If hover, click to open

      h1.set(x, y + h + 32);
      h1.drawHeader();
      h1.drawSubheader();
      //subheader("IMMERSIVE EXPERIENCE", x, y + h + 32 + 20);

    }

    class Header{
      constructor(text, subtext){
        this.text = text;
        this.subtext = subtext;
        this.x = 0;
        this.y = 0;
        this.dy = 0.5;
        this.fontSize = 24;
        this.offset = 24 + 4;
        this.switch = false;
        this.jumble = "";
        this.count = 0;
        this.fps = 60;
      }
      set(x, y){
        this.x = x;
        this.y = y;

      }
      drawHeader(){
        c.font = `500 ${this.fontSize}px GT America`;

        c.save();
          const rect = new Path2D();
          rect.rect(this.x, this.y -26, c.measureText(this.text).width, 28);
          c.clip(rect);
          c.fillStyle = "rgba(244, 244,244, 1)";
          c.fill();

          if(this.x < canvas.width/2 && this.x + 400 > canvas.width/2 && !this.switch){
            this.offset -= this.dy;
            if(this.offset <= 20){
              this.dy *= 0.975;
              if(this.offset < 0.5){
                this.dy = 0;
                this.offset = 0;
                this.switch = true;
              }
            }
          }
          else{
            if(this.offset > -(this.fontSize + 4) && this.offset < this.fontSize + 4){
              this.dy = 0.5;
              this.offset -= this.dy;
            }
          }
          if(this.offset <= -(this.fontSize + 4)){
            this.offset = this.fontSize + 4;
            this.switch = false;
          }

          c.fillText(this.text,this.x, this.y - this.offset);
        c.restore();
    }

    //MIGHT WANT TO CHANGE AFTER
    drawSubheader(){
        const collection = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";

        if(this.fps > 4){
          if(this.x < canvas.width/2 && this.x + 400 > canvas.width/2){
            //Jumble into randomness of length text
            if(this.jumble.length < this.subtext.length){
              var newString = "";
              for(var i = 0; i < this.jumble.length; i++){
                newString += collection[Math.floor(Math.random() * collection.length)];
              }
              this.jumble = newString;
              this.jumble += collection[Math.floor(Math.random() * collection.length)];
            }
            else if (this.jumble.length >= this.subtext.length && this.jumble != this.subtext){
              //Jumble into the text itself
              var newString = "";
              if(this.count < this.subtext.length){
                newString += this.subtext.substring(0, this.count + 1);
                this.count++;
              }
              for(var i = this.count + 1; i <= this.subtext.length; i++){
                newString += collection[Math.floor(Math.random() * collection.length)];
              }

              this.jumble = newString;
            }
          }
          else{
            //Jumble into nothingness
            this.count = 0;
            var newString = "";
            for(var i = this.count + 1; i <= this.jumble.length; i++){
              newString += collection[Math.floor(Math.random() * collection.length)];
            }

            this.jumble = newString;
            this.jumble = this.jumble.substring(0, this.jumble.length - 1);
            //console.log('working');
          }

          this.fps = 0;
        }

        c.font = "400 12px GT America Mono";
        c.fillStyle = "rgba(244, 244, 244, 1)";
        c.fill();
        c.fillText(this.jumble,this.x, this.y + 20);

        this.fps++;
      }


  }

  function drawNav(w){
    var rem = 16;
    var length = Math.floor(w/rem);
    var h = canvas.height - (4*rem);
    c.save();
      //Move origin to bottom center
      c.translate(canvas.width/2 - w/2, h);

      //Current project out of x being viewed
      c.font = "400 12px GT America Mono";
      c.fillStyle = "rgba(244, 244, 244, 1)";
      c.fill();
      c.fillText('0' + projectIndex,0, 8);
      c.fillText('0' + 5,w - 24, 8);

      const rect = new Path2D();
      rect.rect(100, -12.5, 200, 50);
      c.clip(rect);

      //Iterate by project total * 5
      for(var i = 0; i <= length; i++){
        var x = i*rem + posx/rem;
        c.beginPath();
        //Height is function of position
        if(x > w/2 - 10 && x <= w/2){
          c.moveTo(x, -2);
          c.lineTo(x, 10);
        }
        else if(x > w/2 - 30 && x < w/2 + 10){
          c.moveTo(x, -1);
          c.lineTo(x, 9);
        }
        else{
          c.moveTo(x, 0);
          c.lineTo(x, 8);
        }
        c.strokeStyle = "#f4f4f4";
        c.stroke();
      }

      //Left Gradient
      const g1 = c.createLinearGradient(100, 0, 200, 0);
      g1.addColorStop(0, 'rgba(0,0,0,1)');
      g1.addColorStop(1, 'rgba(0,0,0,0)');

      //Right Gradient
      const g2 = c.createLinearGradient(200, 0, 300, 0);
      g2.addColorStop(0, 'rgba(0,0,0,0)');
      g2.addColorStop(1, 'rgba(0,0,0,1)');

      c.fillStyle = g1;
      c.fillRect(100, -2, 100, 20);

      c.fillStyle = g2;
      c.fillRect(200, -2, 100, 20);
    c.restore();
  }

  //Probably want to load these from a text file
  var header1 = new Header("THOUGHT POLICE", "IMMERSION");
  var header2 = new Header("LA BATIE", "WEB DESIGN");
  var header3 = new Header("ITALIST", "UX DESIGN");
  var header4 = new Header("DEAR VAN GOGH", "IMMERSIVE ENVIRONMENT");
  var header5 = new Header("LAMPANELLA", "INDUSTRIAL DESIGN");

  //Animates Everything
  function draw(){
      image(c, 1, header1);
      image(c, 2, header2);
      image(c, 3, header3);
      image(c, 4, header4);
      image(c, 5, header5);

      drawNav(400);
  }

  function animate(){
    requestAnimationFrame(animate);

    c.clearRect(0, 0, canvas.width, canvas.height);

    //Limit distance
    if(posx != mouse.distance){
      if((mouse.dx < 0 && posx > -(canvas.width - 160)) || (mouse.dx > 0 && posx < 440)){
        posx += mouse.dx;
        mouse.dx *= 0.97;
      }
    }
    //draw
    draw();
  }

  animate();
  });
  return(
    <canvas ref={canvasRef} id="canvas" width={0} height={0}></canvas>
  )
}
